class Accordion{constructor(e){this.accordion=e;this.addClass=(element,className)=>{element.classList.add(className)};this.addEvent=(e,type,handler)=>{if(e.attachEvent){e.attachEvent('on'+type,handler)}else{e.addEventListener(type,handler)}};this.addEventListenersToDT=(e)=>{this.addEvent(e,'click',()=>{const nextElemSibling=e.nextElementSibling;if(nextElemSibling.classList.contains('is-expanded')){this.toggleClassName(nextElemSibling,'is-expanded')}else{this.contractAllDefinitions();this.toggleClassName(nextElemSibling,'is-expanded')}})};this.toggleClassName=(e,className)=>{e.classList.toggle(className)};this.contractAllDefinitions=()=>{const elements=document.getElementsByClassName('is-expanded');Array.prototype.forEach.call(elements,e=>{e.classList.remove('is-expanded')})};this.init=()=>{this.addClass(this.accordion,'Accordion');for(let i=0;i<this.accordion.children.length;i++){let e=this.accordion.children[i];if(e.tagName==='DT'){this.addClass(e,'Accordion-dt');this.addEventListenersToDT(e)}
if(e.tagName==='DD'){this.addClass(e,'Accordion-dd')}}};this.init()}
addNewContent(title,data){const dt=document.createElement('dt');this.addClass(dt,'Accordion-dt');this.addClass(dt,'is-animated');dt.innerHTML=title;this.addEventListenersToDT(dt);const dd=document.createElement('dd');this.addClass(dd,'Accordion-dd');const p=document.createElement('p');p.innerHTML=data;dd.appendChild(p);this.accordion.appendChild(dt);this.accordion.appendChild(dd);setTimeout(()=>dt.classList.remove('is-animated'),1000)}}